import e from 'express';
import React from 'react'
import {useForm} from '../hooks/useForm'
const initialValue = {
    firstName: "",
    lastName: "",
    address: "",
    city: "",
    state: "",
    zip: "",
  };
const CheckoutForm = (props) => {
    const [handleChanges,values,showSuccessMessage,setShowSuccessMessage, setValues, formvalues, setFormValues] = useForm(initialValue);
    const handleSubmit = (e) => {
        e.preventDefault();
       setShowSuccessMessage(true);
              
      };
    const resetForm = () => {
     
      setValues(initialValue)
    }
    
    console.log(formvalues)
    return (
        <div className="checkout">
            
        <form onSubmit={handleSubmit }>
        <h2>Checkout Form</h2>
    
          
          <input
            name="firstName"
            value={values.firstName}
            onChange={handleChanges}
            placeholder="First Name"
          />
        
        <div className="baseline" />
    
         
          <input
            name="lastName"
            value={values.lastName}
            onChange={handleChanges}
            placeholder="Last Name"
          />
        
        <div className="baseline" />
    
          
          <input
            name="address"
            value={values.address}
            onChange={handleChanges}
            placeholder="Address"
          />
        
        <div className="baseline" />
    
         
          <input name="city" value={values.city} onChange={handleChanges} placeholder="City" />
        
        <div className="baseline" />
    
          
          <input name="state" value={values.state} onChange={handleChanges} placeholder="State" />
        
        <div className="baseline" />
    
          
          <input name="zip" value={values.zip} onChange={handleChanges} placeholder="Zip" />
        
        <div className="baseline" />
        <button className="md-button form-button">Update</button>
      </form>
      {showSuccessMessage && (
        
        <div className="success-message" data-testid="successMessage">
          <p>
            Thank you for placing your order! Woo-hoo! <span role="img">ðŸŽ‰</span>
          </p>
          <p>Your new items will be shipped to:</p>
          <br />
          <br />
          <p>
            {formvalues.firstName} {formvalues.lastName}
          </p>
          <p>{formvalues.address}</p>
          <p>
            {formvalues.city}, {formvalues.state} {formvalues.zip}
          </p>
        </div>
      )}
            
        </div>
    )
}

export default CheckoutForm;
