import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Route, NavLink, Switch } from "react-router-dom";
import axios from "axios";

import Home from "./components/Home";
import ItemsList from "./components/ItemsList";
import Item from "./components/Item";
import ItemForm from "./components/ItemForm";
import UpdateForm from "./components/UpdateForm";
import ShoppingCart from "./components/ShoppingCart";
import "./styles.css";

const App = () => {
  const [cart, setCart] = useState([]);
  const [items, setItems] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:3333/items")
      .then((res) => setItems(res.data))
      .catch((error) => console.log(error));
  }, []);
  // const addItem = (itemy) => {
  //   setCart(...cart, itemy )
  // }

  return (
    <div className="App">
      <nav>
        <h1 className="store-header">Dustin's Trinkets</h1>
        <div className="nav-links">
          <NavLink exact to="/item-form">
            Add Item
          </NavLink>
          <NavLink exact to="/">
            Home
          </NavLink>
          <NavLink to="/item-list">Shop</NavLink>
          <NavLink to="/shopping-cart">Cart</NavLink>
        </div>
      </nav>
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>

        <Route path="/shopping-cart">
          <ShoppingCart items={items} />
        </Route>

        <Route exact path="/item-list">
          <ItemsList  items={items} />
        </Route>

        <Route path="/item-list/:id">
          <Item items={items} setItems={setItems} />
        </Route>

        <Route path="/item-form">
          <ItemForm />
        </Route>
        <Route path="/update-form/:id">
          <UpdateForm setItems={setItems} />
        </Route>
      </Switch>


    </div>
  );
};

const rootElement = document.getElementById("root");
ReactDOM.render(
  <Router>
    <App />
  </Router>,
  rootElement
);
